    "def confidence_interval(data, z):\n",
    "    mean = np.mean(data)\n",
    "    std = np.std(data)\n",
    "    n = len(data)\n",
    "    SE = std/((n)**0.5)#computes the standard error using standard error formula\n",
    "    lowbound = mean - (z)*(SE)#computes the lower bound\n",
    "    upbound = mean + (z)*(SE)#computes the upper bound\n",
